import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import Dashboard from './pages/Dashboard';
import ResidentPortal from './pages/ResidentPortal';
import GuestPortal from './pages/GuestPortal';
import { setAuthToken } from './utils/api'; // Import the setAuthToken function

/**
 * ProtectedRoute component to ensure only authenticated users can access certain routes.
 */
const ProtectedRoute = ({ children }) => {
  const token = localStorage.getItem('token');

  // If no token exists, redirect to the login page
  if (!token) {
    return <Navigate to="/" />;
  }

  // Set the token globally in Axios headers
  setAuthToken(token);

  return children;
};

function App() {
  // On app load, check if a token exists and set it globally
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setAuthToken(token); // Set the token globally in Axios headers
    }
  }, []);

  return (
    <Router>
      <Routes>
        {/* Public routes */}
        <Route path="/" element={<LoginPage />} />
        <Route path="/register" element={<RegisterPage />} />

        {/* Protected routes */}
        <Route
          path="/dashboard"
          element={
            <ProtectedRoute>
              <Dashboard />
            </ProtectedRoute>
          }
        />
        <Route
          path="/resident-portal"
          element={
            <ProtectedRoute>
              <ResidentPortal />
            </ProtectedRoute>
          }
        />
        <Route
          path="/guest-portal"
          element={
            <ProtectedRoute>
              <GuestPortal />
            </ProtectedRoute>
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
