import React, { useEffect, useState } from 'react';
import { getProperties } from '../utils/api'; // Import centralized API function

function Dashboard() {
  const [properties, setProperties] = useState([]); // State to store properties
  const [loading, setLoading] = useState(true); // State to track loading status
  const [error, setError] = useState(null); // State to track errors

  useEffect(() => {
    /**
     * Fetch properties from the backend.
     */
    const fetchProperties = async () => {
      try {
        const data = await getProperties(); // Use centralized API function
        setProperties(data); // Update state with fetched properties
      } catch (err) {
        console.error('Error fetching properties:', err.response?.data?.message || 'An error occurred');
        setError(err.response?.data?.message || 'Failed to load properties'); // Set error message
      } finally {
        setLoading(false); // Stop loading regardless of success or failure
      }
    };

    fetchProperties(); // Call the fetch function
  }, []);

  return (
    <div className="p-8">
      {/* Dashboard Header */}
      <h1 className="text-3xl font-bold mb-6">Property Manager Dashboard</h1>

      {/* Loading State */}
      {loading && (
        <div className="flex justify-center items-center h-40">
          <span className="text-lg text-gray-600">Loading properties...</span>
        </div>
      )}

      {/* Error State */}
      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative" role="alert">
          <strong className="font-bold">Error:</strong>
          <span className="block sm:inline"> {error}</span>
        </div>
      )}

      {/* Properties List */}
      {!loading && !error && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {properties.length > 0 ? (
            properties.map((property) => (
              <div key={property._id} className="bg-white p-6 rounded shadow-md">
                <h2 className="text-xl font-bold">{property.name}</h2>
                <p className="text-gray-600">{property.address}</p>
                <p className="mt-2">
                  <strong>Status:</strong> {property.status || 'Active'}
                </p>
                <p className="mt-2">
                  <strong>Parking Spots:</strong> {property.parkingSpots || 'N/A'}
                </p>
              </div>
            ))
          ) : (
            <div className="col-span-full text-center text-gray-600">
              No properties found. Please create a new property.
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default Dashboard;
